import React, { useEffect, useState } from 'react'
import axios from 'axios'
import { Bar } from '@vx/shape'
import { Group } from '@vx/group'
import { AxisBottom, AxisLeft } from '@vx/axis'
import { LinearGradient } from '@vx/gradient'
import { scaleBand, scaleLinear } from '@vx/scale'

function Compb() {
  const [data, setData] = useState([])

  useEffect(() => {
    (async() => {
      let a = await axios.get('https://raw.githubusercontent.com/nychealth/coronavirus-data/master/boro.csv')
      let res = await a.data

      let 
        dump = [],
        lines = res.replace('The ', '').split('\r'),
        headers = lines[0].split(',')

      for (let i = 1; i < lines.length; i++) {
        let 
          obj = {},
          line = lines[i].replace('\n', ''),
          curLine = line.split(',')

        if (curLine[0] === 'Citywide') { continue }

        for (var j = 0; j < headers.length; j++) {
          obj[headers[j].toLowerCase()] = curLine[j]
        }

        dump.push(obj)
      }

      setData(dump)
    })()
  }, [])

  const 
    x = d => d.borough_group,
    y = d => d.covid_case_count,
    height = 400,
    xMax = width,
    yMax = height - 100

  const xScale = scaleBand({
    rangeRound: [0, xMax],
    domain: data.map(x),
    padding: 0.4
  })

  const yScale = scaleLinear({
    rangeRound: [yMax, 0],
    domain: [0, Math.max(...data.map(y))]
  })

  return (
    <>
      <svg width={width} height={height}>
      <LinearGradient 
          from="#b39cea" 
          to="#e99cea" 
          id="gradient"/>
        <rect width={width} height={height} fill="#fbc2eb" rx={14} />
        <Group top={40}>
          {data.map((d) => {
            const 
              boro = x(d),
              barWidth = xScale.bandwidth(),
              barHeight = yMax - yScale(y(d)),
              barX = xScale(boro),
              barY = yMax - barHeight

            return (
              <Group>
                <Bar
                  x={barX}
                  y={barY}
                  width={barWidth}
                  height={barHeight}
                  fill={`url(#gradient)`}
                />
                <text
                  x={xScale(x(d))}
                  y={yMax - barHeight}
                  dx={"5%"}
                  dy={"-2%"}
                  textAnchor="middle"
                  fill="#53486b"
                  fontSize={15}
                  fontFamily={"Snoopy"}
                >
                  {parseFloat((parseInt(d.covid_case_count) / 100000) * 100).toPrecision(3).toLocaleString()}%
                </text>
              </Group>
            )
          })}

          <AxisBottom
            hideTicks={true}
            scale={xScale}
            top={yMax}
            stroke={'#ffffff'}
            tickLabelProps={() => ({
              fill: '#000000',
              textAnchor: 'middle',
              fontSize: 18,
              fontFamily: 'Snoopy'
            })}
            tickTextFill={'#ffffff'}
          />

          {/* <AxisLeft
            hideTicks={true}
            scale={yScale}
          /> */}
        </Group>
      </svg>
    </>
  )
}

export default Compb
