import React, { useEffect, useState } from 'react'
import axios from 'axios'
import { Group } from '@vx/group'
import { scaleTime, scaleLinear } from '@vx/scale'
import { AreaClosed } from '@vx/shape'
import { AxisLeft, AxisBottom } from '@vx/axis'
import { LinearGradient } from '@vx/gradient'
import { extent, max } from 'd3-array'

function Compa() {
  const [data, setData] = useState([])

  useEffect(() => {
    (async() => {
      let a = await axios.get('https://api.coindesk.com/v1/bpi/historical/close.json')
      let res = await a.data["bpi"]

      let data = Object.keys(res).map((key) => {
        return {
          date: key,
          close: res[key]
        }
      })

      setData(data)
    })()
  }, [])

  const width = 750
  const height = 400

  const x = d => new Date(d.date)
  const y = d => d.close

  const margin = {
    top: 60,
    bottom: 60,
    left: 80,
    right: 80,
  }

  const xMax = width - margin.left - margin.right
  const yMax = height - margin.top - margin.bottom

  const xScale = scaleTime({
    range: [0, xMax],
    domain: extent(data, x)
  })

  const yScale = scaleLinear({
    range: [yMax, 0],
    domain: [0, max(data, y)],
  })

  return (
    <>
      <svg width={width} height={height}>
        <LinearGradient
          from='#fbc2eb'
          to='#a6c1ee'
          id='gradient'
        />

        <Group top={margin.top} left={margin.left}>

          <AreaClosed
            data={data}
            x={d => xScale(x(d))}
            y={d => yScale(y(d))}
            yScale={yScale}
            fill={"url(#gradient)"}
            stroke={""}
          />

          <AxisLeft
            scale={yScale}
            top={0}
            left={0}
            label={'Close Price ($)'}
            stroke={'#1b1a1e'}
            tickTextFill={'#1b1a1e'}
          />

          <AxisBottom
            scale={xScale}
            top={yMax}
            label={'Years'}
            stroke={'#1b1a1e'}
            tickTextFill={'#1b1a1e'}
          />

        </Group>
      </svg>
    </>
  )
}

export default Compa
