import React, { useEffect, useState } from 'react'
import axios from 'axios'
import { Bar } from '@vx/shape'
import { Group } from '@vx/group'
import { AxisBottom, AxisLeft } from '@vx/axis'
import { GradientSteelPurple } from '@vx/gradient'
import { scaleBand, scaleLinear } from '@vx/scale'

function Compc() {
  const [data, setData] = useState([])

  useEffect(() => {
    (async() => {
      let a = await axios.get('https://raw.githubusercontent.com/nychealth/coronavirus-data/master/by-age.csv')
      let res = await a.data

      let 
        dump = [],
        lines = res.replace(/[\sA-Za-z]/gi, '').split('\r'),
        headers = lines[0].split(',')

        console.log(lines)

      for (let i = 1; i < lines.length; i++) {
        let 
          obj = {},
          line = lines[i].replace('\n', ''),
          curLine = line.split(',')

        if (curLine[0].match(/Citywide\s\S+/gi)) { continue }

        for (var j = 0; j < headers.length; j++) {
          obj[headers[j].toLowerCase()] = curLine[j]
        }

        dump.push(obj)
        console.log(dump)
      }

      setData(dump)
    })()
  }, [])

  const 
    x = d => d.borough_group,
    y = d => d.covid_case_count,
    height = window.innerHeight / 2,
    width = window.innerWidth / 2,
    xMax = width,
    yMax = height - 100

  const xScale = scaleBand({
    rangeRound: [0, xMax],
    domain: data.map(x),
    padding: 0.4
  })

  const yScale = scaleLinear({
    rangeRound: [yMax, 0],
    domain: [0, Math.max(...data.map(y))]
  })

  return (
    <>

    </>
  )
}

export default Compc
