import React, { useEffect, useState } from 'react'
import { axisBottom, axisLeft, extent, scaleLinear, scaleTime, select, timeFormat } from 'd3'
import axios from 'axios'

function States() {
  const 
    [dump, dFunc] = useState(),
    [xScale, xFunc] = useState(),
    [yScale, yFunc] = useState(),
    [xAxis, xaFunc] = useState(),
    [yAxis, yaFunc] = useState()

  const
    svg = select("#btc"),
    w = 600, 
    h = 600,
    margin = { top: 40, right: 40, bottom: 40, left: 40 },
    height = h - margin.top - margin.bottom,
    width = w - margin.right - margin.left

  async function dMapped() {
    let a = await axios.get('https://api.coindesk.com/v1/bpi/historical/close.json')
    let res = await a.data["bpi"]
    return Object.keys(res).map((key) => {
          return {
            x: key,
            y: res[key]
          }
      })
  }

  async function scaleFunc(data) {
    let x = scaleTime()
        .domain(extent(data, d => d.x))
        .range([0, width])

    let xAxis = axisBottom(x).tickFormat(timeFormat)

    let y = 
      scaleLinear().domain(extent(data, d => d.y)).range([height - margin.bottom, margin.top])

    let yAxis = axisLeft(y).ticks(data.length)

    return { x, y, xAxis, yAxis }
  }

  useEffect(() => {
    try {
      dMapped()
        .then((ret) => 
        {
          dFunc(ret)

          scaleFunc(ret)
            .then((x, y, xa, ya) => { 
              xFunc(x)
              yFunc(y)
              xaFunc(xa)
              yaFunc(ya)

              svg
                .append("svg")
                .attr("width", width + margin.left + margin.right)
                .attr("height", height + margin.top + margin.bottom)
                .append("g")
                  .attr("transform", "translate(0, " + height + ")")
                  .call(axisBottom(x));
            })
        })
    }

    catch(e) { 
      throw(e)
    }
  }, [])

  return (
    <React.Fragment>
      <div id="btc">

      </div>
    </React.Fragment>
  )
}

export default States
