import React, { useEffect, useState } from 'react'
import axios from 'axios'
import { Group } from '@vx/group'
import { Pie } from '@vx/shape'
import { GradientSteelPurple } from '@vx/gradient'
import { scaleBand, scaleLinear } from '@vx/scale'

function Compc() {
  const [data, setData] = useState([])

  useEffect(() => {
    (async() => {
      let a = await axios.get('https://raw.githubusercontent.com/nychealth/coronavirus-data/master/by-age.csv')
      let res = await a.data

      let 
        dump = [],
        lines = res.split('\r'),
        headers = lines[0].split(',')

      for (let i = 1; i < lines.length; i++) {
        let 
          obj = {},
          line = lines[i].replace('\n', ''),
          curLine = line.split(',')

        if (curLine[0].match(/Citywide\s\S+/gi)) { continue }

        for (var j = 0; j < headers.length; j++) {
          obj[headers[j].toLowerCase()] = curLine[j].replace(/[\sA-Za-z]/gi, '')
        }

        dump.push(obj)
      }

      setData(dump)
    })()
  }, [])

  const 
    height = 400,
    width = 600,
    radius = Math.min(width, height) - 100

  return (
    <>
      <svg width={width} height={height}>
        <GradientSteelPurple id="gradient" />
        <rect width={width} height={height} fill="#fbc2eb" rx={14} />
        <Group top={40}>
          <Pie
            centroid
            data={data}
            fillOpacity={d => 1 / (d.index + 2)}
            outerRadius={radius - 135}
            pieValue={d => d.age_group}
          />
        </Group>
      </svg>
    </>
  )
}

export default Compc
