import { extent, scaleLinear, select } from 'd3'
import axios from 'axios'

function StatesDep() {
  const 
    [dump, dFunc] = useState(),
    [xScale, xFunc] = useState(),
    [yScale, yFunc] = useState(),
    ref = useRef(null),
    g = select(ref.current),
    w = 600, 
    h = 600,
    margin = { top: 40, right: 40, bottom: 40, left: 40 },
    height = h - margin.top - margin.bottom,
    width = w - margin.right - margin.left

  const dMapped = async () => {
    axios
      .get('https://api.coindesk.com/v1/bpi/historical/close.json')
      .then((res) => {
        let mapped = Object.keys(res.data["bpi"]).map((key) => {
          return {
            x: key,
            y: res.data["bpi"][key]
          }
        })

        return mapped
      })
      .catch((e) => { throw(e) })
  }

  async function scaleFunc() {
    let x = scaleLinear()
        .domain(extent(data, d => d.x))
        .range([0, width])

    let y = scaleLinear()
      .domain(extent(data, d => d.y))
      .range([height, 0])

    return { x, y }
  }

  useEffect(() => {    
    try { 
      let d = dMapped()
      dFunc(d)
    } catch(e) { 
      throw(e) 
    } finally {}
  }, [])

