import React, { useEffect, useRef, useState } from 'react'
import { extent, scaleLinear, select } from 'd3'
import axios from 'axios'

function StatesDep() {
  const 
    [dump, dFunc] = useState(),
    ref = useRef(null),
    g = select(ref.current),
    w = 600, 
    h = 600,
    margin = { top: 40, right: 40, bottom: 40, left: 40 },
    height = h - margin.top - margin.bottom,
    width = w - margin.right - margin.left

  const maps = () => {
    try {
      let res = axios
        .get('https://api.coindesk.com/v1/bpi/historical/close.json')
        .then((resData) => {
          let d = Object.keys(resData.data["bpi"]).map((key) => {
              return {
                x: key,
                y: res.data["bpi"][key]
              }
            })
          })

      return d
    } catch (e) { throw(e) }
  }

  const scaleFunc = (data) => {
    let x = scaleLinear()
        .domain(extent(data, d => d.x))
        .range([0, width])

    let y = scaleLinear()
      .domain(extent(data, d => d.y))
      .range([height, 0])

    return { x, y }
  }

  useEffect(() => {    
    try { 
      let d = maps()
      dFunc(d)

      const { xScale, yScale } = scaleFunc(d)
      console.log(xScale, yScale)

    } catch(e) { 
      throw(e) 
    }
  }, [])

  return (
    <React.Fragment>
      {/* <svg width={w} height={h}>
        <g transform={`translate(${margin.left}, ${margin.top})`}>
        </g>
      </svg> */}
      <h1>{JSON.stringify(dump)}</h1>
    </React.Fragment>
  )
}

export default StatesDep
