import React, { useEffect, useState } from 'react'
import axios from 'axios'
import { Group } from '@vx/group'
import { Pie } from '@vx/shape'
import { LinearGradient } from '@vx/gradient'

function Compc() {
  const [data, setData] = useState([])

  useEffect(() => {
    (async() => {
      let a = await axios.get('https://raw.githubusercontent.com/nychealth/coronavirus-data/master/by-age.csv')
      let res = await a.data

      let 
        dump = [],
        lines = res.split('\r'),
        headers = lines[0].split(',')

      for (let i = 1; i < lines.length; i++) {
        let 
          obj = {},
          line = lines[i].replace('\n', ''),
          curLine = line.split(',')

        if (curLine[0].match(/Citywide\s\S+/gi)) { continue }

        for (var j = 0; j < headers.length; j++) {
          obj[headers[j].toLowerCase()] = curLine[j].replace(/[\sA-Za-z]/gi, '')
        }

        dump.push(obj)
      }

      setData(dump)
    })()
  }, [])

  const 
    height = 400,
    width = 600,
    radius = Math.min(width, height) - 100

  return (
    <>
      <svg width={width} height={height}>
        <LinearGradient 
          from="#b39cea" 
          to="#e99cea" 
          id="gradient"/>
        <rect width={width} height={height} fill="#fbc2eb" rx={14} />
        <Group top={height / 2} left={width / 2}>
          <Pie
            fill={`url('#gradient')`}
            centroid={(centroid, arc) => {
              const [x, y] = centroid
              return (
                <Label x={x} y={y}>
                  {arc.data.age_group}
                </Label>
              )
            }}
            data={data}
            outerRadius={radius - 125}
            pieValue={d => d.death_rate}
          />
        </Group>
      </svg>
    </>
  )
}

export default Compc
