import React, { useEffect, useState } from 'react'
import axios from 'axios'
import { Bar } from '@vx/shape'
import { Group } from '@vx/group'
import { AxisBottom } from '@vx/axis'
import { GradientPurpleTeal } from '@vx/gradient'
import { scaleBand, scaleLinear } from '@vx/scale'

function Compb() {
  const [data, setData] = useState([])

  useEffect(() => {
    (async() => {
      let a = await axios.get('https://raw.githubusercontent.com/nychealth/coronavirus-data/master/boro.csv')
      let res = await a.data

      let 
        dump = [],
        lines = res.split('\r'),
        headers = lines[0].split(',')

      for (let i = 1; i < lines.length; i++) {
        let 
          obj = {},
          line = lines[i].replace('\n', '').replace('The ', ''),
          curLine = line.split(',')

        for (var j = 0; j < headers.length; j++) {
          obj[headers[j].toLowerCase()] = curLine[j]
        }

        dump.push(obj)
      }

      setData(dump)
    })()
  }, [])

  const 
    x = d => d.borough_group,
    y = d => d.covid_case_count,
    height = window.innerHeight / 2,
    width = window.innerWidth / 2,
    xMax = width,
    yMax = height - 100

  const xScale = scaleBand({
    rangeRound: [0, xMax],
    domain: data.map(x),
    padding: 0.4
  })

  const yScale = scaleLinear({
    rangeRound: [yMax, 0],
    domain: [0, Math.max(...data.map(y))]
  })

  return (
    <>
      <svg width={width} height={height}>
        <GradientPurpleTeal id="purple" />
        <rect width={width} height={height} fill="#fbc2eb" rx={14} />
        <Group top={40}>
          {data.map((d) => {
            const 
              boro = x(d),
              barWidth = xScale.bandwidth(),
              barHeight = yMax - yScale(y(d)),
              barX = xScale(boro),
              barY = yMax - barHeight

            return (
              <Bar
                l
                x={barX}
                y={barY}
                width={barWidth}
                height={barHeight}
                fill="#ffffff"
              />
            )
          })}
          <AxisBottom
            hideTicks={true}
            scale={xScale}
            top={yMax}
            stroke={'#1b1a1e'}
            tickLabelProps={() => ({
              fill: '#000000',
              textAnchor: 'middle',
              fontSize: 14,
              fontFamily: 'Snoopy'
            })}
            tickTextFill={'#1b1a1e'}
          />
        </Group>
      </svg>
    </>
  )
}

export default Compb
