import React, { useEffect, useState } from 'react'
import { extent, max } from 'd3-array'
import { scaleLinear, scaleTime } from '@vx/scale'
import { LinePath } from '@vx/shape'
import axios from 'axios'

function States() {
  const 
    [dump, dFunc] = useState(),
    [xScale, xFunc] = useState(),
    [yScale, yFunc] = useState()

  const
    width = window.innerWidth / 3, 
    height = window.innerHeight / 2,
    padding = 100,
    xSelector = d => new Date(d.x),
    ySelector = d => d.y

  async function dMapped() {
    let a = await axios.get('https://api.coindesk.com/v1/bpi/historical/close.json')
    let res = await a.data["bpi"]
    return Object.keys(res).map((key) => {
          return {
            x: key,
            y: res[key]
          }
      })
  }

  useEffect(() => {
    async function scaleFunc(data) {
      let x = scaleTime({
        domain: extent(data, d => new Date(d.x)),
        range: [padding, width - padding]
      })

      let y = scaleLinear({
        domain: [0, max(data, d => d.y) + ((max(data, d => d.y) / 3))],
        range: [height - padding, padding]
      })

      return { x, y }
    }

    try {
      dMapped()
        .then((ret) => {
          dFunc(ret)

          scaleFunc(ret)
            .then((x, y) => { 
              xFunc(x)
              yFunc(y)
            })
        })
    }

    catch(e) { 
      throw(e)
    }
  }, [height, width])

    return (
      <>
        <svg width={width} height={height}>
          <rect x={0} y={0} width={width} height={height} fill="#b57edc" />
          <LinePath
            data={dump}
            x={d => xScale(d => new Date(d.x))}
            y={d => yScale(d => d.y)}
            strokeWidth={5}
            stroke="#ffffff"
            strokeLinecap="round"
            fill="transparent"
          />
        </svg>
      </>
    )
}

export default States
