import React, { useEffect, useState } from 'react'
import { axisBottom, axisLeft, extent, scale, scaleLinear, scaleTime, select, timeFormat } from 'd3'
import axios from 'axios'

function States() {
  const 
    [dump, dFunc] = useState(),
    [xScale, xFunc] = useState(),
    [yScale, yFunc] = useState(),
    [xAxis, xaFunc] = useState(),
    [yAxis, yaFunc] = useState()

  const
    svg = select("#btc"),
    w = window.innerWidth / 2.5, 
    h = 500,
    margin = { top: 40, right: 40, bottom: 40, left: 40 },
    height = h - margin.top - margin.bottom,
    width = w - margin.right - margin.left

  async function dMapped() {
    let a = await axios.get('https://api.coindesk.com/v1/bpi/historical/close.json')
    let res = await a.data["bpi"]
    return Object.keys(res).map((key) => {
          return {
            x: key,
            y: res[key]
          }
      })
  }

  async function scaleFunc(data) {
    let x = scaleTime({
      domain: (extent(data, d => new Date(d.x))),
      range: [0, width]
    })

    let y = scaleLinear({
      domain: (extent(data, d => d.y)),
      range: [height - margin.bottom, margin.top]
    })

    let xAxis = axisBottom(x).tickFormat(timeFormat)
    let yAxis = axisLeft(y).ticks(data.length)

    return { x, y, xAxis, yAxis }
  }

  useEffect(() => {
    try {
      dMapped()
        .then((ret) => 
        {
          dFunc(ret)

          scaleFunc(ret)
            .then((x, y, xa, ya) => { 
              xFunc(x)
              yFunc(y)
              xaFunc(xa)
              yaFunc(ya)
            })
        })
    }

    catch(e) { 
      throw(e)
    }
  }, [])

  return (
    <React.Fragment>
      <svg width={width} height={height}>
        <rect x={0} y={0} width={width} height={height} fill="#b57edc" />
      </svg>
    </React.Fragment>
  )
}

export default States
