import React, { useRef, useEffect, useState } from 'react'
import { axisBottom, csv, curveCardinal, line, scaleLinear, select } from 'd3'
import '../styles/compa.css'

function States() {
  const d = csv('https://github.com/nytimes/covid-19-data/blob/master/us-states.csv')

  const [data, setData] = useState([10, 25, 50, 63, 91, 44, 71])
  const svgRef = useRef()

  useEffect(() => {
    const svg = select(svgRef.current)

    const lineFunc = line()
      .x((value, index) => index * 50)
      .y(value => 150 - value)
      .curve(curveCardinal)

    svg.selectAll("path")
      .data([data])
      .join("path")
      .attr("d", lineFunc)
      .attr("fill", "none")
      .attr("stroke", "purple")
  }, [data])

  return (
    <React.Fragment>
      <svg ref={svgRef} />
    </React.Fragment>
  )
}

export default States
