import React, { useEffect, useRef, useState } from 'react'
import { extent, scaleLinear, select } from 'd3'
import axios from 'axios'

function States() {
  const 
    [dump, dFunc] = useState(),
    ref = useRef(null),
    g = select(ref.current),
    w = 600, 
    h = 600,
    margin = { top: 40, right: 40, bottom: 40, left: 40 },
    height = h - margin.top - margin.bottom,
    width = w - margin.right - margin.left

  var
    xScale = null,
    yScale = null

  async function xy () {
    let maps = await init()

    xScale = scaleLinear()
      .domain(extent(maps, d => d.x))
      .range([0, width])

    yScale = scaleLinear()
      .domain(extent(maps, d => d.y))
      .range([height, 0])

