import React, { useEffect, useRef, useState } from 'react'
import { extent, scaleLinear, select } from 'd3'
import axios from 'axios'

function States() {
  const 
    [dump, dFunc] = useState(),
    [xScale, xFunc] = useState(),
    [yScale, yFunc] = useState(),
    ref = useRef(null),
    g = select(ref.current),
    w = 600, 
    h = 600,
    margin = { top: 40, right: 40, bottom: 40, left: 40 },
    height = h - margin.top - margin.bottom,
    width = w - margin.right - margin.left

  const dMapped = async () => {
    let a = await axios.get('https://api.coindesk.com/v1/bpi/historical/close.json')
    let res = await a.data["bpi"]
    return Object.keys(res).map((key) => {
          return {
            x: key,
            y: res[key]
          }
      })
  }

  async function scaleFunc(data) {
    let x = scaleLinear()
        .domain(extent(data, d => d.x))
        .range([0, width])

    let y = scaleLinear()
      .domain(extent(data, d => d.y))
      .range([height, 0])

    return { x, y }
  }

  useEffect(() => {    
    try { 
      dMapped()
        .then((ret) => 
        {
          dFunc(ret)

          scaleFunc(ret)
            .then((x, y) => {
              xFunc(x)
              yFunc(y)
            })
        })
    } 

