import React, { useEffect, useState } from 'react'
import { axisBottom, axisLeft, extent, scaleLinear, select } from 'd3'
import axios from 'axios'

function States() {
  const [dump, dFunc] = useState()

  const 
    w = 600,
    h = 600,
    margin = {
    top: 40,
    right: 40,
    bottom: 40,
    left: 40
  }

  const
    width = w - margin.right - margin.left,
    height = h - margin.top - margin.bottom

  async function axFunc() {
    await axios
      .get('https://api.coindesk.com/v1/bpi/historical/close.json')
      .then((res) => {
        let maps = Object.keys(res.data["bpi"]).map((key) => {
          return {
            x: key,
            y: res.data["bpi"][key]
          }
        })

        dFunc(maps)
      })
      .catch(e => console.log(e))
  }

  useEffect(() => {
    try { axFunc() } catch {}

    const xScale = scaleLinear()
      .domain(extent(dump, d => d.x))
      .range([0, width])

    const yScale = scaleLinear()
      .domain(extent(dump, d => d.y)).nice()
      .range([height, 0])
  }, [])

  return (
    <React.Fragment>
      <svg width={w} height={h}>
        <g transform={`translate(${margin.left}, ${margin.top})`}>
        </g>
      </svg>
    </React.Fragment>
  )
}

export default States
