import React, { useEffect, useState } from 'react'
import axios from 'axios'
import { localPoint } from '@vx/event'
import { LinearGradient } from '@vx/gradient'
import { Group } from '@vx/group'
import { Pie } from '@vx/shape'
import { useTooltip, TooltipWithBounds } from '@vx/tooltip'

const Compc = () => {
  const [data, setData] = useState([])
  const {
    tooltipData,
    tooltipLeft,
    tooltipTop,
    tooltipOpen,
    showTooltip,
    hideTooltip
  } = useTooltip()

  useEffect(() => {
    (async() => {
      let a = await axios.get('https://raw.githubusercontent.com/nychealth/coronavirus-data/master/by-age.csv')
      let res = await a.data

      let 
        dump = [],
        lines = res.split('\r'),
        headers = lines[0].split(',')

      for (let i = 1; i < lines.length; i++) {
        let 
          obj = {},
          line = lines[i].replace('\n', ''),
          curLine = line.split(',')

        if (curLine[0].match(/Citywide\s\S+/gi)) continue

        for (var j = 0; j < headers.length; j++) {
          obj[headers[j].toLowerCase()] = curLine[j].replace(/[\sA-Za-z]/gi, '')
        }

        dump.push(obj)
      }

      setData(dump)
    })()
  }, [])

  const 
    width = 650,
    height = 400,
    radius = Math.min(width, height) - 100

  const Label = ({ x, y, children }) => {
    if (children === "75") { children = "75+" }

    return (
      <text
        fontFamily={"Snoopy"}
        fill="white"
        textAnchor="middle"
        x={x}
        y={y}
        dy="-.25em"
        fontSize={16}
      >
        {Math.abs(x) < 10 ? null : children}
      </text>
    )
  }

  const handleMouseOver = (event, datum) => {
    const coords = localPoint(event.target.ownerSVGElement, event)
    showTooltip({
      tooltipLeft: coords.x,
      tooltipTop: coords.y,
      tooltipData: datum
    })
  }

  return (
    <>
      <div className="svgWrapper">
        <svg width={width} height={height}>
          <LinearGradient 
            from="#b39cea" 
            to="#e99cea" 
            id="gradient"/>
          <rect width={width} height={height} fill="#fbc2eb" rx={14} />
          <Group top={height / 2} left={width / 2}>
            <Pie
              fill={`url('#gradient')`}
              centroid={(centroid, arc) => {
                const [x, y] = centroid
                return (
                  <Label x={x} y={y}>
                    {arc.data.age_group}
                  </Label>
                )
              }}
              stroke={"#ffffff"}
              data={data}
              outerRadius={radius - 115}
              pieValue={d => d.death_rate}
              onMouseOver={handleMouseOver}
              onMouseOut={hideTooltip}
            />
          </Group>
        </svg>
      </div>
      <div className="descDiv">
        <div className="headerWrap">
          <span className="descriptionHeader">Age Groups</span>
        </div>
        <div className="descWrap">
          <span className="description">
            This contains age-specific rates of confirmed cases, hospitalizations, and deaths.
          </span>
        </div>
      </div>
    </>
  )
}

export default Compc
