import React, { useRef, useEffect, useState } from 'react'
import { axisBottom, curveCardinal, line, scaleLinear, select } from 'd3'

function Appa() {
  const [data, setData] = useState([10, 25, 50, 63, 91, 44, 71])
  const svgRef = useRef()
  useEffect(() => {
    const svg = select(svgRef.current)
    const xAxis = axisBottom()

    const xScale = scaleLinear()
      .domain([0, data.length-1])
      .range([0, 300])

    const yScale = scaleLinear()
      .domain([0, 75])
      .range([150, 0])
    const lineFunc = line()
      .x((value, index) => xScale(index))
      .y(value => 150 - value)
      .curve(curveCardinal)
    // svg
    //   .selectAll("circle")
    //   .data(data)
    //   .join("circle")
    //   .attr("r", value => value)
    //   .attr("cx", value => value * 2)
    //   .attr("cy", value => value * 2)
    //   .attr("stroke", "red")
    svg.selectAll("path")
      .data([data])
      .join("path")
      .attr("d", value => lineFunc(value))
      .attr("fill", "none")
      .attr("stroke", "purple")
  }, [data])

  return (
    <React.Fragment>
      <svg ref={svgRef}> 
        <path d="M0,150 100,100 150,120" stroke="purple" fill="none" />
      </svg>
      <br />
      <button onClick={() => setData(data.map(value => value + 5))}>
        Update      
      </button>
      <button onClick={() => setData(data.filter(value => value < 50))}>
        Filter      
      </button>
    </React.Fragment>
  )
}

export default Appa
