import React, { useEffect, useState } from 'react'
import axios from 'axios'
import '../styles/comp.css'
import { AxisBottom, AxisLeft } from '@vx/axis'
import { curveBasis } from '@vx/curve'
import { LinearGradient } from '@vx/gradient'
import { Group } from '@vx/group'
import { scaleTime, scaleLinear } from '@vx/scale'
import { localPoint } from "@vx/event";
import { Line, LinePath } from '@vx/shape'
import { useTooltip } from '@vx/tooltip'
import { bisector } from 'd3-array'

const Compa = () => {
  const [data, setData] = useState([])
  const {
    tooltipData,
    tooltipLeft,
    tooltipTop,
    tooltipOpen,
    showTooltip,
    hideTooltip
  } = useTooltip()

  useEffect(() => {
    (async() => {
      let a = await axios.get('https://raw.githubusercontent.com/nychealth/coronavirus-data/master/case-hosp-death.csv')
      let res = await a.data

      let 
        dump = [],
        lines = res.replace('DATE_OF_INTEREST', 'date').split('\r'),
        headers = lines[0].split(',')

      for (let i = 1; i < lines.length; i++) {
        let
          obj = {},
          line = lines[i].replace('\n', ''),
          curLine = line.split(',')

        for (let j = 0; j < headers.length; j++) {
          obj[headers[j].toLowerCase()] = curLine[j] || 0
        }

        dump.push(obj)
      }

      setData(dump)
    })()
  }, [])

  const 
    margin = {
      top: 40,
      bottom: 40,
      left: 0,
      right: 0
    },
    width = 650,
    height = 400,
    xMax = width - 25,
    yMax = height - 100,
    date = d => new Date(d.date),
    yNew = d => d.new_covid_case_count,
    yHosp = d => d.hospitalized_case_count,
    yDc = d => d.death_count,
    x = d => xScale(date(d)),
    y1 = d => yScale(yNew(d)),
    y2 = d => yScale(yHosp(d)),
    y3 = d => yScale(yDc(d)),
    bisectDate = bisector(x).left

  const xScale = scaleTime({
    domain: [Math.min(...data.map(date)), Math.max(...data.map(date))],
    range: [0, xMax],
    padding: 0.4
  })

  const yScale = scaleLinear({
    domain: [0, Math.max(...data.map(yNew))],
    range: [yMax, 0]
  })

  const handleTooltip = ({ event, data, xSelector, xScale, yScale }) => {
    const { showTooltip } = this.props;
    const { x } = localPoint(event);
    const x0 = xScale.invert(x);
    const index = bisectDate(data, x0, 1);
    const d0 = data[index - 1];
    const d1 = data[index];
    let d = d0;
    if (d1 && d1.date) {
      d = x0 - xSelector(d0) > xSelector(d1) - x0 ? d1 : d0;
    }
    showTooltip({
      tooltipData: d,
      tooltipLeft: xScale(x(d)),
      tooltipTop: yScale(y1(d)),
    })
  }

  return (
    <>
      <div className="svgWrapper">
        <svg width={width} height={height}> 
          <rect x={0} y={0} width={width} height={height} fill="#fbc2eb" rx={14} />
          <LinearGradient 
            from="#b39cea" 
            to="#e99cea" 
            id="gradient"/>
            <Group top={margin.top}>
              <LinePath
                data={data}
                x={x}
                y={y1}
                stroke={'#ffffff'}
                strokeWidth={2}
                curve={curveBasis}
              />
              <LinePath
                hideZero
                data={data}
                x={x}
                y={y2}
                stroke={`url('#gradient')`}
                strokeWidth={1}
                curve={curveBasis}
              />
              <LinePath
                data={data}
                x={x}
                y={y3}
                stroke={'#ff0000'}
                strokeWidth={1}
                curve={curveBasis}
              />
              <AxisBottom
                hideAxisLine
                hideTicks={true}
                scale={xScale}
                top={yMax}
                stroke={'#ffffff'}
                tickLabelProps={() => ({
                  fill: '#000000',
                  textAnchor: 'middle',
                  fontSize: 16,
                  fontFamily: 'Snoopy'
                })}
                tickTextFill={'#ffffff'}
              />
              <AxisLeft
                scale={yScale}
                stroke={'#ffffff'}
                hideTicks={false}
                tickLabelProps={() => ({
                  dx: '1.75em',
                  dy: '-0.75em',
                  fill: '#000000',
                  textAnchor: 'start',
                  fontSize: 14,
                  fontFamily: 'Snoopy'
                })}
                tickTextFill={'#ffffff'}
              />
              {tooltipData && (
                <g>
                  <Line
                    from={{ x: tooltipLeft, y: 0 }}
                    to={{ x: tooltipLeft, y: yMax }}
                    stroke="#5C77EB"
                    strokeWidth={4}
                    style={{ pointerEvents: "none" }}
                    strokeDasharray="4,6"
                  />
                  <circle
                    cx={tooltipLeft}
                    cy={tooltipTop}
                    r={4}
                    fill="#5C77EB"
                    stroke="white"
                    strokeWidth={2}
                    style={{ pointerEvents: "none" }}
                  />
                </g>
              )}
            </Group>
        </svg>
      </div>
      <div className="descDiv">
        <div className="headerWrap">
          <span className="descriptionHeader">Cases, Hospitalizations, Deaths</span>
        </div>
        <div className="descWrap">
          <span className="description">
            Cases are by date of diagnosis<br />
            Hospitalizations are by date of admission<br/>
            Deaths are by date of death<br />
          </span>
        </div>
      </div>
    </>
  )
}

export default Compa
