import React, { useEffect, useRef, useState } from 'react'
import { extent, scaleLinear, select } from 'd3'
import axios from 'axios'

function States() {
  const 
    [dump, dFunc] = useState(),
    ref = useRef(null),
    g = select(ref.current),
    w = 600, 
    h = 600,
    margin = { top: 40, right: 40, bottom: 40, left: 40 },
    height = h - margin.top - margin.bottom,
    width = w - margin.right - margin.left

  var
    xScale = null,
    yScale = null


  async function init() {
    await axios
    .get('https://api.coindesk.com/v1/bpi/historical/close.json')
    .then((res) => {
      dFunc(Object.keys(res.data["bpi"]).map((key) => {
        return {
          x: key,
          y: res.data["bpi"][key]
        }
      }))
    })
    .catch((e) => console.log(e))

    await xy()
  }

  async function xy() {
    await init
    xScale = scaleLinear()
      .domain(extent(dump, d => d.x))
      .range([0, width])

    yScale = scaleLinear()
      .domain(extent(dump, d => d.y))
      .range([height, 0])
  }

  useEffect(() => {
    init()

  }, [])

  return (
    <React.Fragment>
      <svg width={w} height={h}>
        <g transform={`translate(${margin.left}, ${margin.top})`}>
        </g>
      </svg>
      <h1>{JSON.stringify(dump)}</h1>
    </React.Fragment>
  )
}

export default States
