import React, { useEffect, useState } from 'react'
import axios from 'axios'
import '../styles/comp.css'
import { AxisBottom, AxisLeft } from '@vx/axis'
import { curveBasis } from '@vx/curve'
import { LinearGradient } from '@vx/gradient'
import { Group } from '@vx/group'
import { scaleTime, scaleLinear } from '@vx/scale'
import { LinePath } from '@vx/shape'

const Compa = () => {
  const [data, setData] = useState([])

  useEffect(() => {
    (async() => {
      let a = await axios.get('https://raw.githubusercontent.com/nychealth/coronavirus-data/master/case-hosp-death.csv')
      let res = await a.data

      let 
        dump = [],
        lines = res.replace('DATE_OF_INTEREST', 'date').split('\r'),
        headers = lines[0].split(',')

      for (let i = 1; i < lines.length; i++) {
        let
          obj = {},
          line = lines[i].replace('\n', ''),
          curLine = line.split(',')

        for (let j = 0; j < headers.length; j++) {
          obj[headers[j].toLowerCase()] = curLine[j] || 0
        }

        dump.push(obj)
      }

      setData(dump)
    })()
  }, [])

  const 
    margin = {
      top: 40,
      bottom: 40,
      left: 0,
      right: 0
    },
    width = 650,
    height = 400,
    xMax = width - 25,
    yMax = height - 100,
    date = d => new Date(d.date),
    yNew = d => d.new_covid_case_count,
    yHosp = d => d.hospitalized_case_count,
    yDc = d => d.death_count,
    x = d => xScale(date(d)),
    y1 = d => yScale(yNew(d)),
    y2 = d => yScale(yHosp(d)),
    y3 = d => yScale(yDc(d))

  const xScale = scaleTime({
    domain: [Math.min(...data.map(date)), Math.max(...data.map(date))],
    range: [0, xMax],
    padding: 0.4
  })

  const yScale = scaleLinear({
    domain: [0, Math.max(...data.map(yNew))],
    range: [yMax, 0]
  })

  return (
    <>
      <div className="svgWrapper">
        <svg width={width} height={height}> 
          <rect x={0} y={0} width={width} height={height} fill="#fbc2eb" rx={14} />
          <LinearGradient 
            from="#b39cea" 
            to="#e99cea" 
            id="gradient"/>
            <Group top={margin.top}>
              <LinePath
                data={data}
                x={x}
                y={y1}
                stroke={'#ffffff'}
                strokeWidth={2}
                curve={curveBasis}
              />
              <LinePath
                hideZero
                data={data}
                x={x}
                y={y2}
                stroke={`url('#gradient')`}
                strokeWidth={1}
                curve={curveBasis}
              />
              <LinePath
                data={data}
                x={x}
                y={y3}
                stroke={'#ff0000'}
                strokeWidth={1}
                curve={curveBasis}
              />
              <Bar
                x={0}
                y={0}
                width={width}
                height={height}
                fill="transparent"
                rx={14}
                data={data}
                onTouchStart={event =>
                  this.handleTooltip({
                    event,
                    x,
                    xScale,
                    yScale,
                    data: data
                  })
                }
                onTouchMove={event =>
                  this.handleTooltip({
                    event,
                    x,
                    xScale,
                    yScale,
                    data: data
                  })
                }
                onMouseMove={event =>
                  this.handleTooltip({
                    event,
                    x,
                    xScale,
                    yScale,
                    data: data
                  })
                }
                onMouseLeave={event => hideTooltip()}
              />
              <AxisBottom
                hideAxisLine
                hideTicks={true}
                scale={xScale}
                top={yMax}
                stroke={'#ffffff'}
                tickLabelProps={() => ({
                  fill: '#000000',
                  textAnchor: 'middle',
                  fontSize: 16,
                  fontFamily: 'Snoopy'
                })}
                tickTextFill={'#ffffff'}
              />
              <AxisLeft
                scale={yScale}
                stroke={'#ffffff'}
                hideTicks={false}
                tickLabelProps={() => ({
                  dx: '1.25em',
                  dy: '-0.25em',
                  fill: '#000000',
                  textAnchor: 'start',
                  fontSize: 14,
                  fontFamily: 'Snoopy'
                })}
                tickTextFill={'#ffffff'}
              />
            </Group>
            {tooltipData && (
              <g>
                <Line
                  from={{ x: tooltipLeft, y: 0 }}
                  to={{ x: tooltipLeft, y: yMax }}
                  stroke="rgba(92, 119, 235, 1.000)"
                  strokeWidth={2}
                  style={{ pointerEvents: 'none' }}
                  strokeDasharray="2,2"
                />
                <circle
                  cx={tooltipLeft}
                  cy={tooltipTop + 1}
                  r={4}
                  fill="black"
                  fillOpacity={0.1}
                  stroke="black"
                  strokeOpacity={0.1}
                  strokeWidth={2}
                  style={{ pointerEvents: 'none' }}
                />
                <circle
                  cx={tooltipLeft}
                  cy={tooltipTop}
                  r={4}
                  fill="rgba(92, 119, 235, 1.000)"
                  stroke="white"
                  strokeWidth={2}
                  style={{ pointerEvents: 'none' }}
                />
              </g>
            )}
        </svg>
      </div>
      <div className="descDiv">
        <div className="headerWrap">
          <span className="descriptionHeader">Cases, Hospitalizations, Deaths</span>
        </div>
        <div className="descWrap">
          <span className="description">
            Cases are by date of diagnosis<br />
            Hospitalizations are by date of admission<br/>
            Deaths are by date of death<br />
          </span>
        </div>
      </div>
    </>
  )
}

export default Compa
