import React, { useEffect, useState } from 'react'
import { axisBottom, axisLeft, extent, max, scaleLinear, scaleTime, timeFormat } from 'd3'
import { LinePath } from '@vx/shape'
import axios from 'axios'

function States() {
  const 
    [dump, dFunc] = useState(),
    [xScale, xFunc] = useState(),
    [yScale, yFunc] = useState(),
    [xAxis, xaFunc] = useState(),
    [yAxis, yaFunc] = useState()

  const
    w = window.innerWidth / 3, 
    h = window.innerHeight / 2,
    height = h - margin.top - margin.bottom,
    width = w - margin.right - margin.left

  async function dMapped() {
    let a = await axios.get('https://api.coindesk.com/v1/bpi/historical/close.json')
    let res = await a.data["bpi"]
    return Object.keys(res).map((key) => {
          return {
            x: key,
            y: res[key]
          }
      })
  }

  async function scaleFunc(data) {
    let x = scaleTime({
      domain: (extent(data, d => new Date(d.x))),
      range: [0, width]
    })

    let y = scaleLinear({
      domain: [0, max(data, d => d.y) + (max(data, d => d.y) / 3)],
      range: [height, 0]
    })

    let xAxis = axisBottom(x).tickFormat(timeFormat)
    let yAxis = axisLeft(y).ticks(data.length)

    return { x, y, xAxis, yAxis }
  }

  useEffect(() => {
    try {
      dMapped()
        .then((ret) => {
          dFunc(ret)

          scaleFunc(ret)
            .then((x, y, xa, ya) => { 
              xFunc(x)
              yFunc(y)
              xaFunc(xa)
              yaFunc(ya)
            })
        })
    }

    catch(e) { 
      throw(e)
    }
  }, [])

  return (
    <>
      <svg width={width} height={height}>
        <rect x={0} y={0} width={width} height={height} fill="#b57edc" />
        <LinePath
          data={dump}
          xScale={xScale}
          yScale={yScale}
          x={d => new Date(d.x)}
          y={d => d.y}
          strokeWidth={5}
          stroke="red"
          strokeLinecap="round"
          fill="transparent"
        />
      </svg>
    <h1>{JSON.stringify(dump)}</h1>
    </>
  )
}

export default States
