import React, { useEffect, useRef, useState } from 'react'
import { extent, scaleLinear, select } from 'd3'
import axios from 'axios'

function States() {
  const 
    [dump, dFunc] = useState(),
    ref = useRef(null)

  useEffect(() => {
    g = select(ref.current),
    w = 600, 
    h = 600,
    margin = { top: 40, right: 40, bottom: 40, left: 40 },
    height = h - margin.top - margin.bottom,
    width = w - margin.right - margin.left
    async function axDump() {
      let res = await axios.get('https://api.coindesk.com/v1/bpi/historical/close.json')
      let maps = Object.keys(res.data["bpi"]).map((key) => 
        {
          return {
            x: key,
            y: res.data["bpi"][key]
          }
        })

      return maps
    }

    async function init() {
      let maps = await axDump()
      dFunc(maps)

      xFunc(`
        scaleLinear()
          .domain(extent(maps, d => d.x))
          .range([0, width])`
      )

      yFunc (
        scaleLinear()
          .domain(extent(maps, d => d.y))
          .range([height, 0])
      ) 

    }

    try { init() } 
    catch(e) { throw(e) }

  }, [])

  return (
    <React.Fragment>
      <svg width={w} height={h}>
        <g transform={`translate(${margin.left}, ${margin.top})`}>
        </g>
      </svg>
      <h1>{JSON.stringify(dump)}</h1>
      <p>{xScale} {yScale}</p>
    </React.Fragment>
  )
}

export default States
